import React from 'react';
import ReactDOM from 'react-dom';
import mapboxgl from 'mapbox-gl';

mapboxgl.accessToken = 'pk.eyJ1IjoibHVrZWpheSIsImEiOiJjazVoM3JwaTMwZXJiM2t0ZDZyZnF5bnN3In0.NQ71qNFEXZZzlOhYyWlIPg'

const options = [{
  name: 'Population'
}, {
  name: 'GDP'
}]

class Application extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      lng: -82.56582048445668,
      lat: 35.61540402873807,
      zoom: 12
    };
  }
  componentDidMount() {
  const map = new mapboxgl.Map({
  container: this.mapContainer,
  style: 'mapbox://styles/mapbox/streets-v11',
  center: [this.state.lng, this.state.lat],
  zoom: this.state.zoom
  });

  map.on('move', () => {
  this.setState({
  lng: map.getCenter().lng.toFixed(4),
  lat: map.getCenter().lat.toFixed(4),
  zoom: map.getZoom().toFixed(2)
  });
  });

}
render() {

  const renderOptions = (option, i) => {
        return (
          <label key={i} className="toggle-container">
            <input onChange={() => this.setState({ active: options[i] })}  name="toggle" type="radio" />
            <div className="toggle txt-s py3 toggle--active-white">{option.name}</div>
          </label>
        );
      }

return (
<div>
<div ref={el => this.mapContainer = el} className="mapContainer" />
</div>
)
}
}

ReactDOM.render(<Application />, document.getElementById('app'));
